import React , {useState,useEffect} from 'react'
import products from '../sampleProducts'
import styled from 'styled-components';


const StyledSelectedProductSingle = styled.div`


  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-image: url("https://images.unsplash.com/photo-1601205343500-69822f1ffac4?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=400&q=60");
  background-size: cover;
  background-position: center;



.calendar {
  position: absolute;
  width: 200px;
  height: 250px;
  bottom: 100px;
  right:0;
  background: #fff;
  text-align: center;
  border-radius: 8px;
  overflow: hidden;
  -webkit-box-reflect: below 1px linear-gradient(transparent, transparent, #000400);
}

.calendar #monthName {
  position: relative;
  padding: 5px 10px;
  background: #000000;
  color: #fff;
  font-size: 30px;
}

.calendar #dayName {
  margin: 20px;
  font-size: 20px;
  font-weight: 300;
  color: #464646;
}

.calendar #dayNumber {
  margin: 0;
  line-height: 1em;
  font-size: 80px;
  font-weight: 700;
  color: #2c2c2c;
}

.calendar #year {
  margin-bottom: 20px;
  font-size: 20px;
  font-weight: 700;
  color: #999;
}

`;

const SelectedProductSingle = ({match}) => {

  const [dogs, setdogs] = useState([{name:'bailey',age:1},{name:'bella',age:3},{name:'piper',age:10},])
useEffect(() => {
 console.log('ok did a thing hook effect')
}, [dogs])
useEffect(() => {
  const lang = navigator.language;

  let date = new Date();

  let dayNumber = date.getDate();
  let month = date.getMonth();
  let dayName = date.toLocaleString(lang, {
      weekday: 'long'
  })
  let monthName = date.toLocaleString(lang, {
      month: 'long'
  })
  let year = date.getFullYear()

  document.getElementById('monthName').innerHTML = monthName;

  document.getElementById('dayName').innerHTML = dayName;

  document.getElementById('dayNumber').innerHTML = dayNumber;

  document.getElementById('year').innerHTML = year;
}, [])

  const product = products.find(p=>p._id === match.params.id)
  return (
    <StyledSelectedProductSingle>
     {/* i am {product ? product.name : <><p>Select a product or view all here <button>Click to view all products</button></p></>} */}
     <h3 style={{color:'white'}}>{product.name}</h3>
     <button onClick={()=>{setdogs([{name:'bailey',age:2},{name:'bella',age:3},{name:'piper',age:10},])}} style={{color:'white'}}>add a yr</button>
  {dogs.map((dog,index)=>(
    <>
    <div style={{backgroundColor:'red',position:'relative',width:'8vw',height:'25vh',display:'flex',flexDirection:'column',lineHeight:'1.45'}}>
   {/* { index === 0 ? <h4 style={{color:'aqua'}} key={index}>{dog.name}, {dog.age}</h4> : <div>dog age {dog.age}</div>} */}
   <header>
  <h1>Cool Articles</h1>
</header>
<div className="band">
  <div className="item-1">
    <a href="https://design.tutsplus.com/articles/international-artist-feature-malaysia--cms-26852" className="card">
      <div className="thumb" style="background-image: url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/flex-1.jpg);"></div>
      <article>
        <h1>International Artist Feature: Malaysia</h1>
        <span>Mary Winkler</span>
      </article>
    </a>
  </div>
  <div className="item-2">
    <a href="https://webdesign.tutsplus.com/articles/how-to-conduct-remote-usability-testing--cms-27045" className="card">
      <div className="thumb" style="background-image: url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/users-2.png);"></div>
      <article>
        <h1>How to Conduct Remote Usability Testing</h1>
        <span>Harry Brignull</span>
      </article>
    </a>
  </div>
  <div className="item-3">
    <a href="https://design.tutsplus.com/articles/envato-tuts-community-challenge-created-by-you-july-edition--cms-26724" className="card">
      <div className="thumb" style="background-image: url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/flex-5.jpg);"></div>
      <article>
        <h1>Created by You, July Edition</h1>
        <p>Welcome to our monthly feature of fantastic tutorial results created by you, the Envato Tuts+ community! </p>
        <span>Melody Nieves</span>
      </article>
    </a>
  </div>
  <div className="item-4">
    <a href="https://webdesign.tutsplus.com/tutorials/how-to-code-a-scrolling-alien-lander-website--cms-26826" className="card">
      <div className="thumb" style="background-image: url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/landing.png);"></div>
      <article>
        <h1>How to Code a Scrolling “Alien Lander” Website</h1>
        <p>We’ll be putting things together so that as you scroll down from the top of the page you’ll see an “Alien Lander” making its way to touch down.</p>
        <span>Kezz Bracey</span>
      </article>
    </a>
  </div>
  <div className="item-5">
    <a href="https://design.tutsplus.com/tutorials/stranger-things-inspired-text-effect--cms-27139" className="card">
      <div className="thumb" style="background-image: url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/strange.jpg);"></div>
      <article>
        <h1>How to Create a “Stranger Things” Text Effect in Adobe Photoshop</h1>
        <span>Rose</span>
      </article>
    </a>
  </div>
  <div className="item-6">
    <a href="https://photography.tutsplus.com/articles/5-inspirational-business-portraits-and-how-to-make-your-own--cms-27338" className="card">
      <div className="thumb" style="background-image: url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/flor.jpg);"></div>
      <article>
        <h1>5 Inspirational Business Portraits and How to Make Your Own</h1>

