import React , {useState,useEffect} from 'react'
import products from '../sampleProducts'
import styled from 'styled-components';


const StyledSelectedProductSingle = styled.div`


  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-image: url("https://images.unsplash.com/photo-1601205343500-69822f1ffac4?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=400&q=60");
  background-size: cover;
  background-position: center;



.calendar {
  position: absolute;
  width: 200px;
  height: 250px;
  bottom: 100px;
  right:0;
  background: #fff;
  text-align: center;
  border-radius: 8px;
  overflow: hidden;
  -webkit-box-reflect: below 1px linear-gradient(transparent, transparent, #000400);
}

.calendar #monthName {
  position: relative;
  padding: 5px 10px;
  background: #000000;
  color: #fff;
  font-size: 30px;
}

.calendar #dayName {
  margin: 20px;
  font-size: 20px;
  font-weight: 300;
  color: #464646;
}

.calendar #dayNumber {
  margin: 0;
  line-height: 1em;
  font-size: 80px;
  font-weight: 700;
  color: #2c2c2c;
}

.calendar #year {
  margin-bottom: 20px;
  font-size: 20px;
  font-weight: 700;
  color: #999;
}




header {
  width: 90%;
  max-width: 1240px;
  margin: 0 auto;
}

.band {
  width: 90%;
  max-width: 1240px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: auto;
  grid-gap: 20px;
}
@media (min-width: 30em) {
  .band {
    grid-template-columns: 1fr 1fr;
  }
}
@media (min-width: 60em) {
  .band {
    grid-template-columns: repeat(4, 1fr);
  }
}

.card {
  background: white;
  text-decoration: none;
  color: #444;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  display: -webkit-box;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
          flex-direction: column;
  min-height: 100%;
  position: relative;
  top: 0;
  -webkit-transition: all .1s ease-in;
  transition: all .1s ease-in;
}
.card:hover {
  top: -2px;
  box-shadow: 0 4px 5px rgba(0, 0, 0, 0.2);
}
.card article {
  padding: 20px;
  -webkit-box-flex: 1;
          flex: 1;
  display: -webkit-box;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
          flex-direction: column;
  -webkit-box-pack: justify;
          justify-content: space-between;
}
.card h1 {
  font-size: 20px;
  margin: 0;
  color: #333;
}
.card p {
  -webkit-box-flex: 1;
          flex: 1;
  line-height: 1.4;
}
.card span {
  font-size: 12px;
  font-weight: bold;
  color: #999;
  text-transform: uppercase;
  letter-spacing: .05em;
  margin: 2em 0 0 0;
}
.card .thumb {
  padding-bottom: 60%;
  background-size: cover;
  background-position: center center;
}

@media (min-width: 60em) {
  .item-1 {
    grid-column: 1 / span 2;
  }
  .item-1 h1 {
    font-size: 24px;
  }
}

`;

const SelectedProductSingle = ({match}) => {

  const [dogs, setdogs] = useState([{name:'bailey',age:1},{name:'bella',age:3},{name:'piper',age:10},])
useEffect(() => {
 console.log('ok did a thing hook effect')
}, [dogs])
useEffect(() => {
  const lang = navigator.language;

  let date = new Date();

  let dayNumber = date.getDate();
  let month = date.getMonth();
  let dayName = date.toLocaleString(lang, {
