import React , {useState,useEffect} from 'react'
import products from '../sampleProducts'
import styled from 'styled-components';


const StyledSelectedProductSingle = styled.div`


  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-image: url("https://images.pexels.com/photos/2448749/pexels-photo-2448749.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260");
  background-size: cover;
  background-position: center;


.calendar {
  position: relative;
  width: 200px;
  background: #fff;
  text-align: center;
  border-radius: 8px;
  overflow: hidden;
  -webkit-box-reflect: below 1px linear-gradient(transparent, transparent, #000400);
}

.calendar #monthName {
  position: relative;
  padding: 5px 10px;
  background: #000000;
  color: #fff;
  font-size: 30px;
}

.calendar #dayName {
  margin: 20px;
  font-size: 20px;
  font-weight: 300;
  color: #464646;
}

.calendar #dayNumber {
  margin: 0;
  line-height: 1em;
  font-size: 80px;
  font-weight: 700;
  color: #2c2c2c;
}

.calendar #year {
  margin-bottom: 20px;
  font-size: 20px;
  font-weight: 700;
  color: #999;
}

`;

const SelectedProductSingle = ({match}) => {

  const [dogs, setdogs] = useState([{name:'bailey',age:1},{name:'bella',age:3},{name:'piper',age:10},])
useEffect(() => {
 console.log('ok did a thing hook effect')
}, [dogs])
useEffect(() => {
  const lang = navigator.language;

  let date = new Date();

  let dayNumber = date.getDate();
  let month = date.getMonth();
  let dayName = date.toLocaleString(lang, {
      weekday: 'long'
  })
  let monthName = date.toLocaleString(lang, {
      month: 'long'
  })
  let year = date.getFullYear()

  document.getElementById('monthName').innerHTML = monthName;

  document.getElementById('dayName').innerHTML = dayName;

  document.getElementById('dayNumber').innerHTML = dayNumber;

  document.getElementById('year').innerHTML = year;
}, [])

  const product = products.find(p=>p._id === match.params.id)
  return (
    <StyledSelectedProductSingle>
     {/* i am {product ? product.name : <><p>Select a product or view all here <button>Click to view all products</button></p></>} */}
     <h3 style={{color:'white'}}>{product.name}</h3>
     <button onClick={()=>{setdogs([{name:'bailey',age:2},{name:'bella',age:3},{name:'piper',age:10},])}} style={{color:'white'}}>add a yr</button>
  {dogs.map((dog,index)=>(
    <>
   { index === 0 ?<h4 style={{color:'aqua'}} key={index}>{dog.name}, {dog.age}</h4> : <>dog age {dog.age}</>}
    </>
  ))}
     <div className="calendar">
        <p id="monthName"></p>
        <p id="dayName"></p>
        <p id="dayNumber"></p>
        <p id="year"></p>
    </div>
    </StyledSelectedProductSingle>
  )
}

export default SelectedProductSingle
